static inline mf_t get_spectrum(int i, mf_t lambda)
{
  float res[MF_COUNT];
  const float *lf = (float *)&lambda;
  for(int k=0;k<MF_COUNT;k++)
  {
    int l = (lf[k] - lambda_min)/lambda_step;
    if(l < 0 || l >= lambda_num) res[k] = 0.0f;
    else res[k] = cobs[i][l];
  }
  return mf_loadu(res);
}

int init(FILE *s, void **data)
{
  cobs_t *t = malloc(sizeof(cobs_t));
  *data = t;
  char c;
  t->slot = s_slot_diffuse;
  t->roughness = 1.0f; // cos^1
  if(fscanf(s, " %c %f", &c, &t->roughness) < 1)
  {
    fprintf(stderr, "[cobs] could not read parameters! expecting: [dgsevr] [roughness]\n");
    return 1;
  }
  int dreggn = fscanf(s, "%*[^\n]\n");

  t->slot = tex_parse_slot(c);
  return dreggn == -1;
}

// extern void cleanup(void *data) { } // std cleanup: free()

float emission(float *roughness, float *em, const float lambda, void *data)
{
  cobs_t *c = (cobs_t *)data;

  if(c->slot == s_slot_emission)
  {
    *roughness = c->roughness;
    return .3333f; // will result in terrible importance sampling.
  }
  else
  {
    *em = 0.0f;
    return 0.0f;
  }
}

float prepare(path_t *p, int vi, void *data)
{
  cobs_t *c = data;
  const float u = p->v[vi].hit.s;
  const float v = p->v[vi].hit.t;

  // get cobs index from uv, or gray for bg
  int i, j;
  i = (int)(14.0f*u) % 14;
  j = (int)(10.0f*v) % 10;

  mf_t val = mf_set1(0.0f);

  if(fmodf(14.0f*u, 1.0f) < 0.1f || fmodf(14.0f*u, 1.0f) > 0.9f || fmodf(10.0f*v, 1.0f) < 0.1f || fmodf(10.0f*v, 1.0f) > 0.9f) val = mf_set1(0.3f);
  else val = get_spectrum(14*j + i, p->lambda);
  tex_set_slot(p, vi, c->slot, val);
  return 1.0f;
}

